import React, { useState } from 'react';

export default function PlayerSimilarityApp() {
  const [guess, setGuess] = useState('');
  const [target, setTarget] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setResult(null);
    setError(null);
    try {
      const response = await fetch('http://localhost:5000/guess', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ guess, target }),
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.error || 'Unknown error');
      setResult(data);
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">NBA Player Similarity Tool</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          placeholder="Your Guess (e.g. Kobe Bryant)"
          value={guess}
          onChange={(e) => setGuess(e.target.value)}
          className="w-full p-2 border rounded"
        />
        <input
          type="text"
          placeholder="Target Player (e.g. Shaquille O'Neal)"
          value={target}
          onChange={(e) => setTarget(e.target.value)}
          className="w-full p-2 border rounded"
        />
        <button type="submit" className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
          Submit Guess
        </button>
      </form>

      {error && <p className="mt-4 text-red-600">Error: {error}</p>}

      {result && (
        <div className="mt-6 p-4 border rounded bg-gray-50">
          {result.score === 100 ? (
            <>
              <p className="text-green-600 font-semibold">ðŸ”¥ You got it!</p>
              <h2 className="mt-2 font-bold">Top 5 Similar Players:</h2>
              <ul className="list-disc ml-6">
                {result.top_5.map(([name, score], idx) => (
                  <li key={idx}>{name} (score: {score})</li>
                ))}
              </ul>
            </>
          ) : (
            <>
              <p>Similarity Score: <span className="font-bold">{result.score}</span></p>
              <h2 className="mt-2 font-bold">Breakdown:</h2>
              <ul className="list-disc ml-6">
                {Object.entries(result.breakdown).map(([key, val]) => (
                  <li key={key}>{key}: {JSON.stringify(val)}</li>
                ))}
              </ul>
            </>
          )}
        </div>
      )}
    </div>
  );
}
