import React, { useState, useEffect } from 'react';
import { Search, Trophy, RotateCcw, Eye, Target, TrendingUp, Users, Calendar, Zap } from 'lucide-react';

const NBAGuessGame = () => {
  const [targetPlayer, setTargetPlayer] = useState('');
  const [guess, setGuess] = useState('');
  const [guessHistory, setGuessHistory] = useState([]);
  const [gameWon, setGameWon] = useState(false);
  const [guessCount, setGuessCount] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [top5Players, setTop5Players] = useState([]);
  const [showAnswer, setShowAnswer] = useState(false);

  // Modern NBA players for random selection
  const modernPlayers = [
    'LeBron James', 'Stephen Curry', 'Kevin Durant', 'Giannis Antetokounmpo',
    'Luka Doncic', 'Jayson Tatum', 'Joel Embiid', 'Nikola Jokic', 'Damian Lillard',
    'Jimmy Butler', 'Kawhi Leonard', 'Anthony Davis', 'Russell Westbrook',
    'James Harden', 'Chris Paul', 'Klay Thompson', 'Draymond Green',
    'Paul George', 'Kyrie Irving', 'Bradley Beal', 'Devin Booker',
    'Donovan Mitchell', 'Ja Morant', 'Trae Young', 'Zion Williamson'
  ];

  const startNewGame = () => {
    const randomPlayer = modernPlayers[Math.floor(Math.random() * modernPlayers.length)];
    setTargetPlayer(randomPlayer);
    setGuess('');
    setGuessHistory([]);
    setGameWon(false);
    setGuessCount(0);
    setError('');
    setTop5Players([]);
    setShowAnswer(false);
  };

  useEffect(() => {
    startNewGame();
  }, []);

  const makeGuess = async () => {
    if (!guess.trim()) return;

    setLoading(true);
    setError('');

    try {
      const response = await fetch('http://127.0.0.1:5000/guess', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          guess: guess.trim(),
          target: targetPlayer
        })
      });

      if (response.ok) {
        const result = await response.json();
        const { score, matched_name, breakdown, message, top_5 } = result;

        const newGuess = {
          name: matched_name || guess.trim(),
          score: score,
          breakdown: breakdown || {}
        };

        setGuessHistory(prev => {
          const updated = [...prev, newGuess];
          return updated.sort((a, b) => b.score - a.score).slice(0, 10);
        });

        setGuessCount(prev => prev + 1);

        if (score === 100) {
          setGameWon(true);
          setTop5Players(top_5 || []);
        }

        setGuess('');
      } else {
        const errorData = await response.json();
        setError(errorData.error || 'Unknown error occurred');
      }
    } catch (err) {
      setError('Connection error. Make sure the Flask server is running on http://127.0.0.1:5000');
    }

    setLoading(false);
  };

  const revealAnswer = async () => {
    if (!targetPlayer) return;

    setLoading(true);
    try {
      const response = await fetch('http://127.0.0.1:5000/guess', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          guess: targetPlayer,
          target: targetPlayer
        })
      });

      if (response.ok) {
        const result = await response.json();
        setTop5Players(result.top_5 || []);
      }
    } catch (err) {
      console.error('Error fetching top 5:', err);
    }

    setShowAnswer(true);
    setLoading(false);
  };

  const getScoreColor = (score) => {
    if (score >= 80) return 'text-green-400';
    if (score >= 60) return 'text-yellow-400';
    if (score >= 40) return 'text-orange-400';
    return 'text-red-400';
  };

  const getScoreGradient = (score) => {
    if (score >= 80) return 'from-green-500 to-green-600';
    if (score >= 60) return 'from-yellow-500 to-yellow-600';
    if (score >= 40) return 'from-orange-500 to-orange-600';
    return 'from-red-500 to-red-600';
  };

  const formatBreakdownItem = (key, value) => {
    const labels = {
      shared_seasons: 'Shared Seasons',
      shared_streak_bonus: 'Consecutive Bonus',
      teammate_years: 'Teammate Years',
      franchise_overlap: 'Team Overlap',
      franchise_tenure_bonus: 'Tenure Bonus',
      archetype: 'Player Archetype',
      position: 'Position',
      draft_diff: 'Draft Era',
      era_diff: 'Career Era',
      career_end_proximity: 'Career End',
      career_length: 'Career Length'
    };

    return labels[key] || key;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 text-white">
      <div className="container mx-auto px-4 py-8 max-w-6xl">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center gap-3 mb-4">
            <Target className="w-8 h-8 text-orange-400" />
            <h1 className="text-4xl font-bold bg-gradient-to-r from-orange-400 to-red-400 bg-clip-text text-transparent">
              NBA Similarity Game
            </h1>
            <Trophy className="w-8 h-8 text-yellow-400" />
          </div>
          <p className="text-lg text-gray-300 mb-4">
            Guess the mystery NBA player by finding similar players!
          </p>
          <div className="flex items-center justify-center gap-6 text-sm text-gray-400">
            <span className="flex items-center gap-2">
              <Zap className="w-4 h-4" />
              Attempt #{guessCount}
            </span>
            {!gameWon && !showAnswer && (
              <span className="text-gray-500">Mystery Player: ???</span>
            )}
            {(gameWon || showAnswer) && (
              <span className="text-green-400 font-semibold">
                Answer: {targetPlayer}
              </span>
            )}
          </div>
        </div>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* Left Column - Input & Controls */}
          <div className="lg:col-span-1 space-y-6">
            {/* Game Controls */}
            <div className="bg-white/10 backdrop-blur-md rounded-xl p-6 border border-white/20">
              <h3 className="text-xl font-semibold mb-4 flex items-center gap-2">
                <Search className="w-5 h-5" />
                Make Your Guess
              </h3>

              {!gameWon && !showAnswer && (
                <div className="space-y-4">
                  <div className="relative">
                    <input
                      type="text"
                      value={guess}
                      onChange={(e) => setGuess(e.target.value)}
                      onKeyPress={(e) => e.key === 'Enter' && makeGuess()}
                      placeholder="Enter NBA player name..."
                      className="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 text-white placeholder-gray-400"
                      disabled={loading}
                    />
                  </div>

                  <button
                    onClick={makeGuess}
                    disabled={loading || !guess.trim()}
                    className="w-full bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 disabled:from-gray-600 disabled:to-gray-700 py-3 px-6 rounded-lg font-semibold transition-all duration-200 transform hover:scale-105 disabled:scale-100"
                  >
                    {loading ? 'Searching...' : 'Submit Guess'}
                  </button>
                </div>
              )}

              {error && (
                <div className="mt-4 p-3 bg-red-500/20 border border-red-500/30 rounded-lg text-red-200">
                  {error}
                </div>
              )}

              {gameWon && (
                <div className="text-center space-y-4">
                  <div className="text-2xl">ðŸŽ‰</div>
                  <p className="text-green-400 font-semibold text-lg">
                    Congratulations! You found {targetPlayer} in {guessCount} guesses!
                  </p>
                </div>
              )}

              {showAnswer && !gameWon && (
                <div className="text-center space-y-4">
                  <div className="text-2xl">ðŸŽ¯</div>
                  <p className="text-blue-400 font-semibold text-lg">
                    The answer was {targetPlayer}
                  </p>
                </div>
              )}

              <div className="flex gap-3 mt-6">
                <button
                  onClick={startNewGame}
                  className="flex-1 bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 py-2 px-4 rounded-lg font-semibold transition-all duration-200 flex items-center justify-center gap-2"
                >
                  <RotateCcw className="w-4 h-4" />
                  New Game
                </button>

                {!gameWon && !showAnswer && (
                  <button
                    onClick={revealAnswer}
                    className="flex-1 bg-gradient-to-r from-orange-500 to-red-600 hover:from-orange-600 hover:to-red-700 py-2 px-4 rounded-lg font-semibold transition-all duration-200 flex items-center justify-center gap-2"
                  >
                    <Eye className="w-4 h-4" />
                    Reveal
                  </button>
                )}
              </div>
            </div>

            {/* Top 5 Similar Players */}
            {top5Players.length > 0 && (
              <div className="bg-white/10 backdrop-blur-md rounded-xl p-6 border border-white/20">
                <h3 className="text-xl font-semibold mb-4 flex items-center gap-2">
                  <TrendingUp className="w-5 h-5" />
                  Top 5 Most Similar Players
                </h3>
                <div className="space-y-3">
                  {top5Players.map(([name, score], index) => (
                    <div key={name} className="flex items-center justify-between p-3 bg-white/5 rounded-lg">
                      <div className="flex items-center gap-3">
                        <span className="w-6 h-6 bg-gradient-to-r from-yellow-400 to-orange-500 rounded-full flex items-center justify-center text-xs font-bold text-black">
                          {index + 1}
                        </span>
                        <span className="font-medium">{name}</span>
                      </div>
                      <span className={`font-bold ${getScoreColor(score)}`}>
                        {score}/100
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Right Column - Guess History */}
          <div className="lg:col-span-2">
            <div className="bg-white/10 backdrop-blur-md rounded-xl p-6 border border-white/20">
              <h3 className="text-xl font-semibold mb-4 flex items-center gap-2">
                <Users className="w-5 h-5" />
                Guess History ({guessHistory.length})
              </h3>

              {guessHistory.length === 0 ? (
                <div className="text-center py-12 text-gray-400">
                  <Search className="w-12 h-12 mx-auto mb-4 opacity-50" />
                  <p>No guesses yet. Start by entering a player name!</p>
                </div>
              ) : (
                <div className="space-y-4 max-h-96 overflow-y-auto">
                  {guessHistory.map((historyItem, index) => (
                    <div key={index} className="bg-white/5 rounded-lg p-4 border border-white/10 hover:bg-white/10 transition-colors">
                      <div className="flex items-center justify-between mb-3">
                        <h4 className="font-semibold text-lg">{historyItem.name}</h4>
                        <div className={`px-3 py-1 rounded-full text-sm font-bold bg-gradient-to-r ${getScoreGradient(historyItem.score)}`}>
                          {historyItem.score}/100
                        </div>
                      </div>

                      {historyItem.breakdown && Object.keys(historyItem.breakdown).length > 0 && (
                        <div className="grid grid-cols-2 md:grid-cols-3 gap-2 text-xs">
                          {Object.entries(historyItem.breakdown)
                            .filter(([key, value]) => key !== 'total' && key !== 'shared_seasons_detail' && value > 0)
                            .map(([key, value]) => (
                              <div key={key} className="flex justify-between p-2 bg-white/5 rounded">
                                <span className="text-gray-300">{formatBreakdownItem(key, value)}</span>
                                <span className="text-white font-medium">+{value}</span>
                              </div>
                            ))}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NBAGuessGame;
